import codecs

def tupper(x, y):
    """ Tupper's self-referential formula """
    return (0.5 < ((y // 17) // (2 ** (17 * int(x) + int(y) % 17))) % 2)

def tupperTXT(y, on="\u2588", off=" "):
    """ Writes the result of Tupper's self-referential formula in txt
        (from y to y+16)"""

    with codecs.open("tupperTXT.txt", "w", "utf-8") as f:
        array = [[tupper(x, z) for x in range(106)] for z in range(y, y + 17)]
        for row in array:
            for value in row[::-1]:
                f.write(on * value + off * (not value))
            f.write("\n")


